mapper.select:
select * from cn_note
where cn_user_id=#{userId}
  and cn_note_status_id='2'

mapper.delete:
 delete from cn_note
 where cn_note_id in

		#{id}

mapper.select:
select * from cn_note 
where cn_user_id=#{userId}

 and cn_note_title like #{title}


 and cn_note_status_id=#{status}

mapper.update:
update cn_note
set cn_notebook_id=#{bookId}
where cn_note_id=#{noteId}

update cn_note
set cn_note_status_id=#{status}
where cn_note_id=#{noteId}

update cn_note
set cn_note_title=#{cn_note_title},
	cn_note_body=#{cn_note_body},
	cn_note_last_modify_time=#{cn_note_last_modify_time}
where cn_note_id=#{cn_note_id}

mapper.select:
select * from cn_note
where cn_notebook_id=#{bookId}
  and cn_note_status_id='1'
